///////////////////////////////////Define With Animations///////////////////////////////////////////
const hero = document.querySelector(".hero"),
    wolf = document.querySelector(".wolf"),
    score = document.querySelector(".score");
let gameScore = 0;

let jump = () => {
    if (!hero.classList.contains("animate")) {
        hero.classList.add("animate");
        wolf.style.animation = "wolfRun 1s infinite linear";
    }
    setTimeout(() => {
        hero.classList.remove("animate");
    }, 400);
}

window.addEventListener("keydown", (e) => {
    if (e.code === "Space") {
        jump();
        score.textContent = parseInt(gameScore++);
    }
});

function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}

setInterval(() => {
    let heroTop = parseInt(window.getComputedStyle(hero).getPropertyValue("bottom")),
        wolfRight = parseInt(window.getComputedStyle(wolf).getPropertyValue("right"));
    if (heroTop <= 20 && wolfRight > 500) {
        wolf.style.animation = "none";
        gameScore = 0;
        beep();
        // alert("Game Over");
    }
}, 30);































